// ==UserScript==
// @name     Remove doge
// @version  1
// @include  https://twitter.com*
// @run-at   document-end
// ==/UserScript==

function removeDoge() {
  var images = document.getElementsByTagName('svg');
  if (images.length<1) {
  	setTimeout(removeDoge, 0);
  } else {
    if (images.length == 1) {
        setTimeout(removeDoge, 0);
    } else {
      console.log(images[0].outerHTML);
      if (images[0].outerHTML.includes("iVBORw0KGgoAAAANSUh")) {
        images[0].outerHTML = '<svg viewBox="0 0 24 24" aria-hidden="true"><g><pattern height="1" id="0-a" patternContentUnits="objectBoundingBox" width="1"><image height="100" transform="scale(.01 .0087)" width="100" xlink:href="data:image/png;data:image/png;base64,"></image></pattern><path d="M2.412.974h19.176v22.052H2.412z" fill="url(#0-a)"></path></g></svg>';
        console.log("DOGE FOUND!");
      } else {
        setTimeout(removeDoge, 0);
      }
    }
  }
}

console.log('remove doge started!');
removeDoge();
